import React from "react";
import { useState } from "react";
import "./index.scss";

function App() {
  const [counter, setCounter] = useState(0);
  const [red, setRed] = useState(0);
  const [green, setGreen] = useState(0);
  const [blue, setBlue] = useState(100);

  const incrementHandler = () => {
    if (counter < 100) {
      return setCounter(counter + 1);
    }
  };

  const decrementHandler = () => {
    if (counter > -100) {
      return setCounter(counter - 1);
    }
  };
  return (
    <div
      className="App"
      style={{
        background: `linear-gradient(90deg, rgba(255,0,0,1) ${red}%, rgba(0,0,255,1) ${blue}%, rgba(0,255,0,1) ${green}%)`,
      }}
    >
      <div>
        <h2>Счетчик: </h2>
        <h1>{counter}</h1>
        <button onClick={decrementHandler} className="minus">
          - Минус
        </button>
        <button onClick={incrementHandler} className="plus">
          Плюс +
        </button>
      </div>
    </div>
  );
}

export default App;
