import React from "react";
import { useState } from "react";
import "./index.scss";

function App() {
  const [counter, setCounter] = useState(0);

  const incrementHandler = () => {
    if (counter < 20) {
      return setCounter(counter + 1);
    }
  };

  const decrementHandler = () => {
    if (counter > -20) {
      return setCounter(counter - 1);
    }
  };
  return (
    <div className="App" style={{ backgroundColor: `rgb(${0}, ${255}, ${0})` }}>
      <div>
        <h2>Счетчик: </h2>
        <h1>{counter}</h1>
        <button onClick={decrementHandler} className="minus">
          - Минус
        </button>
        <button onClick={incrementHandler} className="plus">
          Плюс +
        </button>
      </div>
    </div>
  );
}

export default App;
