import React from "react";
import { useState } from "react";
import "./index.scss";

function App() {
  const [counter, setCounter] = useState(0);

  const incrementHandler = () => {
    // counter <= 20 ? retursetCounter(counter + 1) : counter;
    if (counter < 20) {
      return setCounter(counter + 1);
    }
  };
  return (
    <div className="App">
      <div>
        <h2>Счетчик: {counter}</h2>
        <h1>0</h1>
        <button className="minus">- Минус</button>
        <button onClick={incrementHandler} className="plus">
          Плюс +
        </button>
      </div>
    </div>
  );
}

export default App;
